void* intal_create(const char* str){
    int length=strlen(str);
    //printf("length=%d",length);
    int i,j=0;
    for(i=0;i<length-1;i++){
        if(str[i]=='0')
            j++;
        else
            break;
    }
    i=0;
    int *arr=(int *)malloc((length+1-j)*sizeof(int));
    for(j;j<length;j++){
        if(str[j]>='0' && str[j]<='9')
            arr[i++]=str[j]-'0';
        else
            return NULL;
    }
    arr[i]=44;
    //printf("%d",arr[i]);
    //void *a=malloc((length+1-j)*sizeof(int));
    void *a=arr;
    //for(i=0;i<length;i++)
      //  printf("%d",((int*)a)[i]);
    return a;

}

void intal_destroy(void* intal){
    free(intal);
}

char* intal2str(void* intal){
    if(intal==NULL){
        return "NaN";
    }
    int l=0,i=0;
    while(((int*)intal)[i]!=44){
        l++;
        i++;
    }
    char* arr=(char *)malloc(l+1);
    for(i=0;i<l;i++){
        arr[i]=((int*)intal)[i]+'0';
    }
    arr[i]='\0';
    return arr;
}

void* intal_increment(void* intal){
    if(intal==NULL)
        return NULL;
    int l=0,k=0;
    while(((int*)intal)[k]!=44){
        l++;
        k++;
    }
    int flag=0,i=l-1;
    while(flag==0 && i>=0 ){
       if(((int*)intal)[i]==9){
            ((int*)intal)[i]=0;
       }
       else{
            ((int*)intal)[i]=((int*)intal)[i]+1;
            flag=1;
       }
       i--;
    }
    if(flag==0){
        int* newarray=(int*)malloc((l+2)*sizeof(int));
        newarray[0]=1;
        for(i=0;i<l;i++){
            newarray[i+1]=0;
        }
        //printf("i=%d",i);
        newarray[i+1]=44;
        free(intal);
        void* intal=malloc((l+2)*sizeof(int));
        intal=newarray;
        return intal;
    }
    return intal;

}

void* intal_decrement(void* intal){
    if(intal==NULL)
        return NULL;
    int l=0,k=0;
    while(((int*)intal)[k]!=44){
        l++;
        k++;
    }
    if(l==1 && ((int*)intal)[0]==0){
        return intal;
        printf("returned intal\n");}
    int flag=0,i=l-1;
    while(flag==0 && i>0 ){
       if(((int*)intal)[i]==0){
            ((int*)intal)[i]=9;
       }
       else{
            ((int*)intal)[i]=((int*)intal)[i]-1;
            flag=1;
       }
       i--;
    }
    if(flag==0){
            if(((int*)intal)[0]==1){
                if(l==1)
                    ((int*)intal)[0]=0;
                else{
                    int* newarray=(int*)malloc((l)*sizeof(int));
                    for(i=0;i<l-1;i++){
                        newarray[i]=9;
                    }
                //printf("i=%d",i);
                    newarray[i]=44;
                    free(intal);
                    void* intal=malloc((l)*sizeof(int));
                    intal=newarray;
                    return intal;
                }
            }
            else
                ((int*)intal)[0]=((int*)intal)[0]-1;
    }
    return intal;

}

void* intal_add(void* intal1, void* intal2){
    int x,l1=0,l2=0,k=0,carry=0;
    if(intal1==NULL || intal2==NULL)
        return NULL;
    while(((int*)intal1)[k]!=44){
        l1++;
        k++;
    }
    k=0;
    while(((int*)intal2)[k]!=44){
        l2++;
        k++;
    }
    int max=l2;
    if(l1>=l2)
        max=l1;
    x=max;
    //int* sum=(int*)malloc(sizeof(int)*(max+1));
    char* result=(char*)malloc(max+2);
    result[max+1]='\0';
    while(l1>0 && l2>0){
        //sum[max]=(((int*)intal1)[l1-1]+((int*)intal2)[l2-1]+carry)%10;
        result[max]=((((int*)intal1)[l1-1]+((int*)intal2)[l2-1]+carry)%10) + '0';
        carry=(((int*)intal1)[l1-1]+((int*)intal2)[l2-1]+carry)/10;
        l1--;l2--;max--;
    }
    while(l1>0){
        //sum[max]=(((int*)intal1)[l1-1]+carry)%10;
        result[max]=((((int*)intal1)[l1-1]+carry)%10) + '0';
        carry=(((int*)intal1)[l1-1]+carry)/10;
        l1--;max--;
    }
    while(l2>0){
        //sum[max]=(((int*)intal2)[l2-1]+carry)%10;
        result[max]=((((int*)intal2)[l2-1]+carry)%10) + '0';
        carry=(((int*)intal2)[l2-1]+carry)/10;
        l2--;max--;
    }
    //sum[0]=carry;
    result[0]=carry+ '0';
    //for(k=0;k<=x;k++)
      //  result[k]=sum[k]+'0';
	return intal_create(result);
}

int intal_compare(void* intal1, void* intal2){
    if(intal1==NULL || intal2==NULL)
        return 0;
    int i,l=0;
    while((((int*)intal1)[l]!=44)&&(((int*)intal2)[l]!=44)){
        l++;
    }
    if(((int*)intal1)[l]!=44)
        return 1;
    else if(((int*)intal2)[l]!=44)
        return -1;
    else{
        for(i=0;i<l;i++){
            if(((int*)intal1)[i]>((int*)intal2)[i])
                return 1;
            else if(((int*)intal1)[i]<((int*)intal2)[i])
                return -1;
        }
    }
    return 0;
}

void* intal_diff(void* intal1, void* intal2){
    if(intal1==NULL || intal2==NULL)
        return NULL;
    int i,j,diff,carry=0;
    char *max,*min,*answer;
    if(intal_compare(intal1,intal2)==1){
        max=intal2str(intal1);min=intal2str(intal2);
    }
    else if(intal_compare(intal1,intal2)==-1){
        max=intal2str(intal2);min=intal2str(intal1);
    }
    else{
       char s[2];
       s[0]='0';s[1]='\0';
       return intal_create(s);
    }
    i=strlen(max)-1;
    answer=(char*)malloc(i+2);
    answer[i+1]='\0';
    for(j=strlen(min)-1;j>=0;j--){
        diff=(max[i]-min[j]-carry);
        if(diff<0){
            carry=1;
            diff=diff+10;
        }
        else
            carry=0;
        answer[i--]=diff+'0';
    }
    for(i;i>=0;i--){
        diff=(max[i]-carry-'0');
        if(diff<0){
            carry=1;
            diff=diff+10;
        }
        else
            carry=0;
        answer[i]=diff+'0';
    }
    return intal_create(answer);
}


void* intal_multiply(void* intal1, void* intal2){
    char *num1=intal2str(intal1);
    char *num2=intal2str(intal2);
    int l1 = strlen(num1);
    int l2 = strlen(num2);
    if (num1[0] == '0' || num2[0] == '0'){
       char s[2];
       s[0]='0';s[1]='\0';
       return intal_create(s);
    }
    int* result=(int*)malloc((l1 + l2)*sizeof(int));
    int x = 0;
    int y = 0;
    int i,j,carry;
    for(i=0;i<l1+l2;i++)
        result[i]=0;
    for(i=l1-1; i>=0; i--){
        carry = 0;
        int x1 = num1[i] - '0';
        y=0;
        for(j=l2-1; j>=0; j--){
            int y1=num2[j] - '0';
            int sum = x1*y1 + result[x+y] + carry;
            carry = sum/10;
            result[x+y] = sum % 10;
            y++;
        }
        if (carry > 0)
            result[x+y] += carry;
        x++;
    }
    i = l1+l2 - 1;
    while (i>=0 && result[i] == 0)
       i--;
    char *str=(char*)malloc(i+2);
    j=0;
    while(i>=0)
        str[j++]=result[i--]+'0';
    str[j]='\0';
    return intal_create(str);
}

void* intal_divide(void* intal1, void* intal2){
    if(intal1==NULL || intal2==NULL)
        return NULL;
    if(((int*)intal2)[0]==0)
        return NULL;
    if(((int*)intal1)[0]==0)
        return intal_create("0");
    if(intal_compare(intal1,intal2)==-1)
        return intal_create("0");
    char *x,*y,*z,*quo,*temp,ch[2];
    void *buf,*mul,*m,*diff;
    x=intal2str(intal1);
    y=intal2str(intal2);
    int l1,l2,i,j,k,comp;
    l1=strlen(x);
    l2=strlen(y);
    z=(char*)malloc(l2+2);
    quo=(char*)malloc(l1-l2+2);
    ch[1]='\0';
    j=l2;i=0;
    for(i;i<l2;i++)
        z[i]=x[i];
    z[i]='\0';
    //printf("first z=%s",z);
    i=0;

    while(j<=l1){
        buf=intal_create(z);
        mul=intal_create(y);
        comp=intal_compare(buf,mul);
        if(comp==-1){
            z[strlen(z)+1]='\0';
            if(j==l1){
                quo[i++]='0';
                //printf("q=%s",quo);
                break;
            }
            z[strlen(z)]=x[j];
            buf=intal_create(z);
            j++;
            quo[i++]='0';
            //printf("second z=%s",z);
        }
        k=0;
        while(intal_compare(buf,mul)>-1){
            ++k;
            if(k<10){
                ch[0]=k+'0';
                m=intal_create(ch);
            }
            else
                m=intal_create("10");
            //printf("m=%d\n",*(int*)m);
            mul=intal_multiply(intal2,m);
            //printf("comp1 %s k=%d\n",intal2str(mul),k);
        }
        //printf("working here %s\n",intal2str(mul));
        if(j==l1){
            if(k>0)
                quo[i++]=(k-1)+'0';
            else
                quo[i++]='0';
            //printf("quo when j==l1 %s\n",quo);
            break;
        }
        if(k==0)
            quo[i++]='0';
        else
            quo[i++]=(k-1)+'0';
        mul=intal_diff(mul,intal2);
        //printf("mul=%s , quo=%s\n",intal2str(mul),quo);
        diff=intal_diff(buf,mul);
        temp=intal2str(diff);
        for(k=0;k<strlen(temp);k++)
            z[k]=temp[k];
        z[k++]=x[j++];
        z[k]='\0';
        //printf("q=%s mul=%s diff=%s z=%s j=%d\n",quo,intal2str(mul),intal2str(diff),z,j);
    }
    quo[i]='\0';
    return intal_create(quo);
}



void* intal_pow(void* intal1, void* intal2){
    void *temp;
    char *str1,*str2;
    str2=intal2str(intal2);
    if(str2[0]=='0'){
        //printf("exit condition\n");
        return intal_create("1");}
    //printf("%s\n",str2);
    temp=intal_pow(intal1,intal_divide(intal2,intal_create("2")));
    //printf("%d %s\n",str2[strlen(str2)-1]-'0',intal2str(temp));
    if((str2[strlen(str2)-1]-'0')%2){
           // printf("entered if\n");
        //printf("mod = 1, temp=%s\n",intal2str(temp));
        return intal_multiply(intal1,intal_multiply(temp,temp));}
    else{
        //printf("entered else\n");
        //printf("mod = 0, %d, temp=%s\n",str2[strlen(str2)-1]-'0',intal2str(temp));
        return intal_multiply(temp,temp);}
}

