#include <stdio.h>
#include "intal.h"
#include <stdlib.h>
#include <string.h>
#include <time.h>
int main()
{

    void *intal, *intal1,*intal2;
    /*char *str1="";*/
    char *str1="pes";
    char*str2="00021";
    //char *str1="00000";
    //char * str2="0010";
    //printf("Enter a string of decimal digits:\n");
    char *str="0009990";
   
    intal= intal_create(str);
    printf("Intal is:%s\n",intal2str(intal));//999
    void *E;
     void *B=intal_increment(intal);
    printf("Intal after incrementing:%s\n",intal2str(B));//1000
   printf("Intal is: %s\n",intal2str(intal));
    printf("Intal after decrementing:");
    
    void *C=intal_decrement(intal);
    void *Z=intal_decrement(C);
    printf("Intal is: %s\n",intal2str(intal));
    
    printf("%s\n",intal2str(Z));
    intal1= intal_create(str1);
    printf("Intal1 is: %s",intal2str(intal1));
    intal2= intal_create(str2);
    printf("\nIntal2 is: %s",intal2str(intal2));
    printf("\nSum is:");
    void *D=intal_add(intal1,intal2);
   printf("%s\n",intal2str(D));

    printf("comparing :");
    int comp=intal_compare(intal1,intal2);
    printf("%d\n",comp);
    printf("Diff is:");
    E=intal_diff(intal1,intal2);
    printf("%s\n", intal2str(E));
    
    
    void *F=intal_multiply(intal1,intal2);
    printf("Product is:%s\n",intal2str(F));
    void *G=intal_divide(intal1,intal2);
    printf("Quotient is:%s\n",intal2str(G));
    void *I=intal_pow(intal1,G);
    printf("Power is:%s\n",intal2str(I));
    intal_destroy(intal);
    intal_destroy(intal1);
    intal_destroy(intal2);
   
    return 0;
}
